mapper.select:
SELECT 

FROM t_goods_activity
WHERE goods_id = #{goodsId,jdbcType=INTEGER}
  AND activity_id = #{activityId,jdbcType=INTEGER}

mapper.delete:
DELETE FROM t_goods_activity
WHERE goods_id = #{goodsId,jdbcType=INTEGER}
  AND activity_id = #{activityId,jdbcType=INTEGER}

mapper.update:
UPDATE t_goods_activity

    update_time = now(),

WHERE goods_id = #{goodsId,jdbcType=INTEGER}
  AND activity_id = #{activityId,jdbcType=INTEGER}

mapper.select:
select DISTINCT act.id, act.name, act.type, act.args, act.detail, act.max, act.value, act.url FROM t_goods_activity ga join t_g_activity act on ga.activity_id = act.id WHERE act.status=1 AND ((act.available = 1 AND act.start_time <= NOW() AND act.end_time >= NOW()) OR act.available = 0) AND ga.goods_id = #{goodsId}
AND ga.goods_id IN
#{goodsId}
