mapper.select:
SELECT g.id, g.name, g.sub_name as subName, g.mark_price as markPrice, g.price, g.sales_num as salesNum, g.img_url as imgUrl, g.detail, g.former_price as formerPrice
,(SELECt count(1)>0 from t_user_keep uk WHERE uk.user_id = #{userId} and uk.obj_id = g.id and uk.type = 1) as keep
FROM t_goods g WHERE g.delete_flag = 0 AND g.status=1 AND id = #{id,jdbcType=INTEGER}

SELECT
  g.id,
  g.name,
  g.sub_name as subName,
  g.mark_price as markPrice,
  g.price,
  g.sales_num as salesNum,
  g.img_url as imgUrl
FROM t_goods g

  LEFT JOIN t_user_keep uk ON(g.id = uk.obj_id AND uk.type=1 AND uk.user_id=#{userId}

WHERE g.delete_flag = 0

  AND g.status = #{status}


    AND g.type_id = #{typeId}


    AND g.type_sub_Id = #{typeSubId}


    AND g.price  >=  #{minPrice}


    AND g.price  <=  #{maxPrice}


    ORDER BY g.#{orderBy},#{asc}


    limit #{start},#{rows}

SELECT
g.id,
g.name,
g.sub_name as subName,
g.mark_price as markPrice,
g.price,
g.sales_num as salesNum,
g.img_url as imgUrl,
g.status
FROM t_goods g
WHERE g.delete_flag = 0

  AND g.type_id = #{typeId}


  AND g.status = #{status}


  AND g.type_sub_Id = #{typeSubId}


  AND g.price  >=  #{minPrice}


  AND g.price  <=  #{maxPrice}


  ORDER BY g.#{orderBy},#{asc}


  limit #{start},#{rows}

SELECT
count(1)
FROM t_goods g
WHERE g.delete_flag = 0

  AND g.type_id = #{typeId}


  AND g.status = #{status}


  AND g.type_sub_Id = #{typeSubId}


  AND g.price  >=  #{minPrice}


  AND g.price  <=  #{maxPrice}
