mapper.select:
SELECT
gts.id,
gts.name,
gts.img,
gts.goods_type as goodsType,
gt.name as typeName
FROM t_goods_type_sub gts LEFT JOIN t_goods_type gt ON (gts.goods_type = gt.id)
WHERE gts.delete_flag = 0

  and gts.goods_type = #{goodsType,jdbcType=TIMESTAMP}


  and gts.sale = #{sale,jdbcType=BIT}


  and gts.rec = #{rec,jdbcType=BIT}


  limit #{start},#{rows}

SELECT gts.id, gts.name, gts.img, gts.sort, gts.sale, gts.rec, gts.goods_type as goodsType, gt.name as typeName, gts.create_time, gts.update_time FROM t_goods_type_sub gts LEFT JOIN t_goods_type gt ON (gts.goods_type = gt.id) WHERE gts.delete_flag = 0
and gts.goods_type = #{goodsType,jdbcType=TIMESTAMP}
and gts.sale = #{sale,jdbcType=BIT}
and gts.rec = #{rec,jdbcType=BIT}
limit #{start},#{rows}

SELECT
  count(1)
FROM t_goods_type_sub
WHERE delete_flag = 0

  and gts.goods_type = #{goodsType,jdbcType=TIMESTAMP}


  and gts.sale = #{sale,jdbcType=BIT}


  and gts.rec = #{rec,jdbcType=BIT}
