mapper.select:
SELECT o.id, o.order_code as orderCode, o.user_id as userId, o.payment_id as paymentId, o.payment_name as paymentName, o.pay_status as payStatus, o.pay_reason as payReason, o.status, o.post_id as postId, o.post_name as postName, o.post_code as postCode, o.money, o.total_money as totalMoney, o.post_money as postMoney, o.lost_activity_money as lostActivityMoney, o.lost_coupon_money as lostCouponMoney, o.lost_money as lostMoney, o.invoice_is as invoiceIs, o.invoice_head as invoiceHead, o.address_id as addressId, o.address, o.zipcode, o.coupon_id as couponId, o.push, o.read, o.form, o.remark, o.explain, o.update_time as updateTime, o.create_time as createTime FROM t_order o WHERE o.delete_flag = 0
AND o.user_id = #{userId}
AND o.delete_flag = #{deleteFlag}
ORDER BY o.#{orderBy}, #{asc}
ORDER BY o.id, desc
limit #{start},#{rows}

SELECT o.id, o.order_code as orderCode, o.user_id as userId, u.account, u.phone, o.money, o.total_money as totalMoney, o.payment_Id as paymentId, o.payment_name as paymentName, o.pay_status as payStatus, o.status, o.post_id as postId, o.post_name as postName, o.update_time as updateTime, o.create_time as createTime FROM t_order o LEFT JOIN t_user u ON(o.user_id = u.id) WHERE o.delete_flag = 0
AND o.payment_Id = #{paymentId}
AND (o.order_code like CONCAT('%','${keyword}','%' ) OR u.account like CONCAT('%','${keyword}','%' ) OR u.phone like CONCAT('%','${keyword}','%' ))
AND o.delete_flag = #{deleteFlag}
AND o.pay_status = #{payStatus}
AND o.status = #{status}
ORDER BY o.#{orderBy} #{asc}
ORDER BY o.create_time desc
limit #{start},#{rows}

SELECT
  count(1)
FROM t_order o

LEFT JOIN t_user u ON(o.user_id = u.id)

WHERE o.delete_flag = 0

  AND o.payment_Id = #{paymentId}


  AND (o.order_code like CONCAT('%','${keyword}','%' ) OR u.account like CONCAT('%','${keyword}','%' ) OR u.phone like CONCAT('%','${keyword}','%' ))


  AND o.pay_status = #{payStatus}


  AND o.status = #{status}
