mapper.select:
SELECT sc.id, sc.user_id AS userId, sc.goods_id AS goodsId, g.name, g.img_url AS imgUrl, g.price, g.mark_price AS markPrice, sc.format_id AS formatId, sc.format_name AS formatName, sc.num, sc.checkbox FROM t_shopping_cart sc LEFT JOIN t_goods g ON(sc.goods_id = g.id) WHERE 1 = 1
AND sc.user_id = #{userId}
AND sc.checkbox = #{checkbox}
ORDER BY #{orderBy} #{desc}
limit #{start},#{rows}

SELECT

FROM t_shopping_cart
WHERE 1 = 1

  AND user_id = #{userId}


  AND goods_id = #{goodsId}


  ORDER BY #{orderBy} #{asc}


  limit #{start},#{rows}
