mapper.update:
UPDATE tb_address
SET
path=#{path},
detail=#{detail}

    ,user_name=#{userName}


    ,telephone=#{telephone}


    ,illustrate=#{illustrate}


    ,delete_flag= #{deleteFlag}

WHERE
id = #{id}

    AND user_id= #{userId}

mapper.select:
select
*
from tb_address where 1=1 AND delete_flag=0

   AND user_id = #{userId}


    AND type = #{type}

ORDER BY create_time DESC

select
*
from tb_address where 1=1 AND delete_flag=0

   AND user_id = #{userId}


   AND type = #{type}

ORDER BY create_time DESC

   limit #{start},#{rows}

  select
	count(1)
  from tb_address where 1=1 AND delete_flag=0

     AND type = #{type}


     AND user_id = #{userId}

  ORDER BY create_time DESC
