mapper.select:
SELECT IFNULL(COUNT(1),0)
FROM tb_shipment_mode
WHERE delete_flag=0 AND shi_type = #{type}

SELECT p.id, p.rel_id as relId, p.rel_type as relType, p.name, p.shi_type as shiType, p.description, p.sort, contact_person as contactPerson, contact_phone as contactPhone, remark_date as remarkDate, p.create_user_id as createUserId, p.update_date as updateDate from tb_shipment_mode as p WHERE p.delete_flag=0
AND p.rel_id=#{relId}
AND rel_type=#{relType}
AND p.name=#{name}
AND shi_type=#{shiType}
AND create_user_id=#{createUserId}
ORDER BY p.sort is null,p.sort ASC,p.update_date DESC
LIMIT #{start}, #{rows}
